import React from "react"

function PopupWithForm({ title, name, children, isOpen, onClose }){

  const handleEscClose = (e) => e.key === "Escape" && onClose(e);

  useEffect(() => {
    if (isOpen) {
      document.addEventListener("keydown", handleEscClose);
    }
    return () => document.removeEventListener("keydown", handleEscClose);
  }, [isOpen]);

  return(
    <div className={isOpen ? `popup popup_${props.name} popup_opened` : `popup_${props.name}`} onClick={props.onClose}>
      <div className="popup__content">
          <button className="popup__close" type="button"></button>
          <h2 className="popup__title">{props.title}</h2>
          <form className={`popup__form popup__form_${props.name}`} name={props.name}>
              {props.children}
              <button className={`popup__save popup__save${props.name}`} type="submit">Создать</button>   
          </form>
      </div>
    </div>
  )
}

export default PopupWithForm
